import React from 'react';
import './App.css';
import Header from './components/Header'
import Contents from './components/Contents'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state={weather:{},
        city:'Coimbatore, IN',
        key:''
        }
}
componentDidMount(){
    console.log(this.state.city)
    this.setState({
      key:localStorage.getItem('key')
    })
    fetch('http://api.openweathermap.org/data/2.5/forecast?q=coimbatore,in&APPID=')
    .then(res => res.json())
    .then(res =>{
        this.setState({weather:res})
    })
}
componentDidUpdate(){
  fetch('http://api.openweathermap.org/data/2.5/forecast?q='+this.state.city+'&APPID=bd47a3b8ecfca25b0777d15a25965418')
  .then(res => res.json())
  .then(res =>{
      this.setState({weather:res})
  })
}

Accept=(city)=>{
  console.log(city)
  this.setState({
    city:city
  })
}


  render()
  {
      return (
      <div className="App">
        <Header update={this.Accept} weather={this.state.weather}/>
        <Contents weather={this.state.weather}/>
      </div>
    );
  }
}

export default App;
