import React from 'react'

class DailyContainer extends React.Component{
    constructor(props){
        super()
    }

    Day(temperature,date,desc,source){
        return(
            <div className='day'>
                <p>{date}</p>
                <img src={source} alt='cannot be loaded'></img>
                <h4>{temperature}</h4>
                <p>{desc}</p>
            </div>
        )
    }

    render(){
        return(
            <div className='daily-container'>
                <div className='daily'>
                    {this.props.data.map(datum=>{
                        let temperature = Math.round(datum.main.temp-273.15);
                        let date = String(new Date(datum.dt_txt)).slice(0,10);
                        let desc = datum.weather[0].description;
                        let source=''
                        if (desc.toLowerCase().includes('clear')){
                            source = '/res/sunny.svg';
                        }
                        else if (desc.toLowerCase().includes('scattered')){
                            source = '/res/sun.svg';
                        }
                        else if (desc.toLowerCase().includes('rain')){
                            source = '/res/storm.svg';
                        }
                        else{
                            source = '/res/cloud.svg';

                        }
                        return this.Day(temperature,date,desc,source)
                    })}
                </div>
            </div>
        )
    }
}
export default DailyContainer
