import React from 'react';
import './App.css';
import Header from './components/Header'
import Contents from './components/Contents'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state={weather:{},
        city:'Coimbatore, IN'
        }
}
componentDidMount(){
    this.setState({
        city:this.props.city
    })
    console.log(this.state.city)
    fetch('http://api.openweathermap.org/data/2.5/forecast?q='+this.state.city+'&APPID=bd47a3b8ecfca25b0777d15a25965418')
    .then(res => res.json())
    .then(res =>{
        this.setState({weather:res})
    })
}
  render()
  {
      return (
      <div className="App">
        <Header weather/>
        <Contents/>
      </div>
    );
  }
}

export default App;
